  

'use strict';

// MAIN //

/**
* Tests if a 32-bit integer is even.
*
* @param {integer32} x - value to test
* @returns {boolean} boolean indicating whether the value is even
*
* @example
* var bool = isEven( 5 );
* // returns false
*
* @example
* var bool = isEven( -2 );
* // returns true
*
* @example
* var bool = isEven( 0 );
* // returns true
*/
function isEven( x ) {
	return (x & 1) === 0;
}


// EXPORTS //

module.exports = isEven;
