  

'use strict';

// MAIN //

/**
* Tests if a 32-bit integer is odd.
*
* @param {integer32} x - value to test
* @returns {boolean} boolean indicating whether the value is odd
*
* @example
* var bool = isOdd( 5 );
* // returns true
*
* @example
* var bool = isOdd( -2 );
* // returns false
*
* @example
* var bool = isOdd( 0 );
* // returns false
*/
function isOdd( x ) {
	return (x & 1) === 1;
}


// EXPORTS //

module.exports = isOdd;
