  

import isComposite = require( './index' );


// TESTS //

// The function returns a boolean...
{
	isComposite( 2 ); // $ExpectType boolean
	isComposite( 3.12 ); // $ExpectType boolean
}

// The compiler throws an error if the function is provided a value other than a number...
{
	isComposite( true ); // $ExpectError
	isComposite( false ); // $ExpectError
	isComposite( null ); // $ExpectError
	isComposite( undefined ); // $ExpectError
	isComposite( [] ); // $ExpectError
	isComposite( {} ); // $ExpectError
	isComposite( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided an unsupported number of arguments...
{
	isComposite(); // $ExpectError
	isComposite( undefined, 123 ); // $ExpectError
}
