  #include "stdlib/math/base/assert/is_infinite.h"
#include <stdio.h>
#include <stdbool.h>

int main( void ) {
	const double x[] = { 3.14, -3.14, 1.0/0.0, 0.0/0.0 };

	bool b;
	int i;
	for ( i = 0; i < 4; i++ ) {
		b = stdlib_base_is_infinite( x[ i ] );
		printf( "Value: %lf. Is infinite? %s.\n", x[ i ], ( b ) ? "True" : "False" );
	}
}
