  

'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var PINF = require( '@stdlib/constants/float64/pinf' );
var NINF = require( '@stdlib/constants/float64/ninf' );
var tryRequire = require( '@stdlib/utils/try-require' );


// VARIABLES //

var isInfinite = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
	'skip': ( isInfinite instanceof Error )
};


// TESTS //

tape( 'main export is a function', opts, function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isInfinite, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided `+infinity`', opts, function test( t ) {
	t.equal( isInfinite( PINF ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `true` if provided `-infinity`', opts, function test( t ) {
	t.equal( isInfinite( NINF ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided an infinite number', opts, function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5.0,
		-1.0e308,
		4.0e-324,
		NaN,
		true,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isInfinite( values[i] ), false, 'returns false' );
	}
	t.end();
});
