  #include "stdlib/math/base/assert/is_infinitef.h"
#include <stdio.h>
#include <stdbool.h>

int main( void ) {
	const float x[] = { 3.14f, -3.14f, 1.0f/0.0f, 0.0f/0.0f };

	bool b;
	int i;
	for ( i = 0; i < 4; i++ ) {
		b = stdlib_base_is_infinitef( x[ i ] );
		printf( "Value: %f. Is infinite? %s.\n", x[ i ], ( b ) ? "True" : "False" );
	}
}
