  #include "stdlib/math/base/assert/is_nanf.h"
#include <stdbool.h>

/**
* Tests if a single-precision floating-point numeric value is `NaN`.
*
* @param x       number
* @return        boolean indicating if an input value is `NaN`
*/
bool stdlib_base_is_nanf( const float x ) {
	return ( x != x );
}
