  

// TypeScript Version: 4.1

/**
* Tests if a single-precision floating-point numeric value is negative zero.
*
* @param x - value to test
* @returns boolean indicating whether the value is negative zero
*
* @example
* var bool = isNegativeZerof( -0.0 );
* // returns true
*
* @example
* var bool = isNegativeZerof( 0.0 );
* // returns false
*/
declare function isNegativeZerof( x: number ): boolean;


// EXPORTS //

export = isNegativeZerof;
