  #include "stdlib/math/base/assert/is_negative_zerof.h"
#include <stdio.h>
#include <stdbool.h>

int main( void ) {
	const float x[] = { 0.0f, -0.0f, 3.14f, 0.0f/0.0f };

	bool b;
	int i;
	for ( i = 0; i < 4; i++ ) {
		b = stdlib_base_is_negative_zerof( x[ i ] );
		printf( "Value: %f. Is -0? %s.\n", x[ i ], ( b ) ? "True" : "False" );
	}
}
