  #include "stdlib/math/base/assert/is_negative_zerof.h"
#include <stdbool.h>
#include <math.h>

/**
* Tests if a single-precision floating-point numeric value is negative zero.
*
* @param x       number
* @return        boolean indicating if an input value is negative zero
*/
bool stdlib_base_is_negative_zerof( const float x ) {
	return ( x == 0.0f && 1.0f/x == -INFINITY );
}
