  

#include "stdlib/math/base/assert/is_positive_zero.h"
#include <stdbool.h>
#include <math.h>

/**
* Tests if a double-precision floating-point numeric value is positive zero.
*
* @param x       number
* @return        boolean indicating if an input value is positive zero
*/
bool stdlib_base_is_positive_zero( const double x ) {
	return ( x == 0.0 && 1.0/x == HUGE_VAL );
}
