  

// TypeScript Version: 4.1

/**
* Tests if a single-precision floating-point numeric value is positive zero.
*
* @param x - value to test
* @returns boolean indicating whether the value is positive zero
*
* @example
* var bool = isPositiveZerof( 0.0 );
* // returns true
*
* @example
* var bool = isPositiveZerof( -0.0 );
* // returns false
*/
declare function isPositiveZerof( x: number ): boolean;


// EXPORTS //

export = isPositiveZerof;
