  

// TypeScript Version: 4.1

/**
* Tests if a number is prime.
*
* @param x - value to test
* @returns boolean indicating whether the value is a prime number
*
* @example
* var bool = isPrime( 11.0 );
* // returns true
*
* @example
* var bool = isPrime( 3.14 );
* // returns false
*/
declare function isPrime( x: number ): boolean;


// EXPORTS //

export = isPrime;
