  

import isPrime = require( './index' );


// TESTS //

// The function returns a boolean...
{
	isPrime( 2 ); // $ExpectType boolean
	isPrime( 3.12 ); // $ExpectType boolean
}

// The compiler throws an error if the function is provided a value other than a number...
{
	isPrime( true ); // $ExpectError
	isPrime( false ); // $ExpectError
	isPrime( null ); // $ExpectError
	isPrime( undefined ); // $ExpectError
	isPrime( [] ); // $ExpectError
	isPrime( {} ); // $ExpectError
	isPrime( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided an unsupported number of arguments...
{
	isPrime(); // $ExpectError
	isPrime( undefined, 123 ); // $ExpectError
}
