  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var isProbability = require( './../lib' );

var bool;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = ( randu()*2.0 ) - 1.0;
	bool = isProbability( x );
	console.log( '%d is %s', x, ( bool ) ? 'a probability' : 'not a probability' );
}
