  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var abs = require( './../lib' );

var rand;
var i;

for ( i = 0; i < 100; i++ ) {
	rand = round( randu() * 100.0 ) - 50.0;
	console.log( 'abs(%d) = %d', rand, abs( rand ) );
}
