  

import abs2f = require( './index' );


// TESTS //

// The function returns a number...
{
	abs2f( 8 ); // $ExpectType number
}

// The compiler throws an error if the function is provided a value other than a number...
{
	abs2f( true ); // $ExpectError
	abs2f( false ); // $ExpectError
	abs2f( null ); // $ExpectError
	abs2f( undefined ); // $ExpectError
	abs2f( '5' ); // $ExpectError
	abs2f( [] ); // $ExpectError
	abs2f( {} ); // $ExpectError
	abs2f( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided insufficient arguments...
{
	abs2f(); // $ExpectError
}
