  

import absf = require( './index' );


// TESTS //

// The function returns a number...
{
	absf( 8 ); // $ExpectType number
}

// The compiler throws an error if the function is provided a value other than a number...
{
	absf( true ); // $ExpectError
	absf( false ); // $ExpectError
	absf( null ); // $ExpectError
	absf( undefined ); // $ExpectError
	absf( '5' ); // $ExpectError
	absf( [] ); // $ExpectError
	absf( {} ); // $ExpectError
	absf( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided insufficient arguments...
{
	absf(); // $ExpectError
}
