  

'use strict';

// MODULES //

var atanh = require( '@stdlib/math/base/special/atanh' );


// MAIN //

/**
* Computes the inverse hyperbolic cotangent of a double-precision floating-point number.
*
* @param {number} x - input value
* @returns {number} inverse hyperbolic cotangent
*
* @example
* var v = acoth( 2.0 );
* // returns ~0.5493
*
* @example
* var v = acoth( 0.0 );
* // returns NaN
*
* @example
* var v = acoth( 0.5 );
* // returns NaN
*
* @example
* var v = acoth( 1.0 );
* // returns Infinity
*
* @example
* var v = acoth( NaN );
* // returns NaN
*/
function acoth( x ) {
	return atanh( 1.0/x );
}


// EXPORTS //

module.exports = acoth;
