  

import cbrtf = require( './index' );


// TESTS //

// The function returns a number...
{
	cbrtf( 8 ); // $ExpectType number
}

// The compiler throws an error if the function is provided a value other than a number...
{
	cbrtf( true ); // $ExpectError
	cbrtf( false ); // $ExpectError
	cbrtf( null ); // $ExpectError
	cbrtf( undefined ); // $ExpectError
	cbrtf( '5' ); // $ExpectError
	cbrtf( [] ); // $ExpectError
	cbrtf( {} ); // $ExpectError
	cbrtf( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided insufficient arguments...
{
	cbrtf(); // $ExpectError
}
