  

import ceilf = require( './index' );


// TESTS //

// The function returns a number...
{
	ceilf( 8 ); // $ExpectType number
}

// The compiler throws an error if the function is provided a value other than a number...
{
	ceilf( true ); // $ExpectError
	ceilf( false ); // $ExpectError
	ceilf( null ); // $ExpectError
	ceilf( undefined ); // $ExpectError
	ceilf( '5' ); // $ExpectError
	ceilf( [] ); // $ExpectError
	ceilf( {} ); // $ExpectError
	ceilf( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided insufficient arguments...
{
	ceilf(); // $ExpectError
}
