  #include "stdlib/math/base/special/clamp.h"
#include <stdio.h>

int main( void ) {
	const double x[] = { 3.14, -3.14, 0.0, 0.0/0.0 };

	double y;
	int i;
	for ( i = 0; i < 4; i++ ) {
		y = stdlib_base_clamp( x[ i ], -3.0, 3.0 );
		printf( "clamp(%lf, %lf, %lf) = %lf\n", x[ i ], -3.0, 3.0, y );
	}
}
