  #include "stdlib/math/base/special/clampf.h"
#include <stdio.h>

int main( void ) {
	const float x[] = { 3.14f, -3.14f, 0.0f, 0.0f/0.0f };

	float y;
	int i;
	for ( i = 0; i < 4; i++ ) {
		y = stdlib_base_clampf( x[ i ], -3.0f, 3.0f );
		printf( "clampf(%f, %f, %f) = %f\n", x[ i ], -3.0f, 3.0f, y );
	}
}
