  

'use strict';

// VARIABLES //

// Ï€ / 180.0
var PI_DIV_180 = 1.7453292519943295e-2;


// MAIN //

/**
* Converts an angle from degrees to radians.
*
* @param {number} x - angle in degrees
* @returns {number} angle in radians
*
* @example
* var r = deg2rad( 90.0 );
* // returns ~1.571
*
* @example
* var r = deg2rad( -45.0 );
* // returns ~-0.785
*
* @example
* var r = deg2rad( NaN );
* // returns NaN
*/
function deg2rad( x ) {
	return x * PI_DIV_180;
}


// EXPORTS //

module.exports = deg2rad;
