// MODULES //

var assign = require( './assign.js' );


// VARIABLES //

var tmp = [ 0.0, 0.0, 0.0, 0.0 ];


// MAIN //

/**
* Compute the Jacobi modulus, am(u, m), of number u with modulus m.
*
* @param {number} u - input value
* @param {number} m - modulus `m`, equivalent to `kÂ²`
* @returns {number} Jacobi amplitude, `am`
*
* @example
* var v = am( 0.3, 0.5 );
* // returns ~0.298
*
* @example
* v = am( 0.0, 0.0 );
* // returns ~0.0
*
* @example
* v = am( Infinity, 1.0 );
* // returns ~1.571
*
* @example
* v = am( 0.0, -2.0 );
* // returns NaN
*
* @example
* v = am( NaN, NaN );
* // returns NaN
*
*/
function am( u, m ) {
	assign( u, m, tmp, 1, 0 );
	return tmp[ 3 ];
}


// EXPORTS //

module.exports = am;
