  

'use strict';

// MODULES //

var assign = require( './assign.js' );


// VARIABLES //

var tmp = [ 0.0, 0.0, 0.0, 0.0 ];


// MAIN //

/**
* Compute the Jacobi elliptic function cn(u, m) of number u with modulus m.
*
* @param {number} u - input value
* @param {number} m - modulus `m`, equivalent to `kÂ²`
* @returns {number} cn(u, m)
*
* @example
* var v = cn( 0.3, 0.5 );
* // returns ~0.956
*
* @example
* v = cn( 0.0, 0.0 );
* // returns ~1.0
*
* @example
* v = cn( Infinity, 1.0 );
* // returns ~0.0
*
* @example
* v = cn( 0.0, -2.0 );
* // returns ~1.0
*
* @example
* v = cn( NaN, NaN );
* // returns NaN
*
*/
function cn( u, m ) {
	assign( u, m, tmp, 1, 0 );
	return tmp[ 1 ];
}


// EXPORTS //

module.exports = cn;
