  

import floorf = require( './index' );


// TESTS //

// The function returns a number...
{
	floorf( 8.3 ); // $ExpectType number
}

// The compiler throws an error if the function is provided a value other than a number...
{
	floorf( true ); // $ExpectError
	floorf( false ); // $ExpectError
	floorf( null ); // $ExpectError
	floorf( undefined ); // $ExpectError
	floorf( '5' ); // $ExpectError
	floorf( [] ); // $ExpectError
	floorf( {} ); // $ExpectError
	floorf( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided insufficient arguments...
{
	floorf(); // $ExpectError
}
