  

import identity = require( './index' );


// TESTS //

// The function returns a number...
{
	identity( 8 ); // $ExpectType number
}

// The compiler throws an error if the function is provided a value other than a number...
{
	identity( true ); // $ExpectError
	identity( false ); // $ExpectError
	identity( null ); // $ExpectError
	identity( undefined ); // $ExpectError
	identity( '5' ); // $ExpectError
	identity( [] ); // $ExpectError
	identity( {} ); // $ExpectError
	identity( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided insufficient arguments...
{
	identity(); // $ExpectError
}
