  'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var isPositiveZero = require( '@stdlib/math/base/assert/is-positive-zero' );
var isNegativeZero = require( '@stdlib/math/base/assert/is-negative-zero' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var PINF = require( '@stdlib/constants/float64/pinf' );
var NINF = require( '@stdlib/constants/float64/ninf' );
var tryRequire = require( '@stdlib/utils/try-require' );


// VARIABLES //

var identity = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
	'skip': ( identity instanceof Error )
};


// TESTS //

tape( 'main export is a function', opts, function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof identity, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function evaluates the identity function when provided a finite number', opts, function test( t ) {
	t.equal( identity( -2.0 ), -2.0, 'returns expected value' );
	t.equal( identity( 3.0 ), 3.0, 'returns expected value' );
	t.end();
});

tape( 'the function evaluates the identity function when provided +-zero', opts, function test( t ) {
	t.equal( isNegativeZero( identity( -0.0 ) ), true, 'returns expected value' );
	t.equal( isPositiveZero( identity( 0.0 ) ), true, 'returns expected value' );
	t.end();
});

tape( 'the function evaluates the identity function when provided +-infinity', opts, function test( t ) {
	t.equal( identity( PINF ), PINF, 'returns expected value' );
	t.equal( identity( NINF ), NINF, 'returns expected value' );
	t.end();
});

tape( 'if provided `NaN`, the function returns `NaN`', opts, function test( t ) {
	var v = identity( NaN );
	t.equal( isnan( v ), true, 'returns NaN' );
	t.end();
});
