  

import invf = require( './index' );


// TESTS //

// The function returns a number...
{
	invf( 8 ); // $ExpectType number
}

// The compiler throws an error if the function is provided a value other than a number...
{
	invf( true ); // $ExpectError
	invf( false ); // $ExpectError
	invf( null ); // $ExpectError
	invf( undefined ); // $ExpectError
	invf( '5' ); // $ExpectError
	invf( [] ); // $ExpectError
	invf( {} ); // $ExpectError
	invf( ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided insufficient arguments...
{
	invf(); // $ExpectError
}
