  #include "stdlib/math/base/special/kronecker_delta.h"
#include <stdio.h>

int main( void ) {
	const double x[] = { 3.0, 4.0, 5.0, 5.0 };

	double y;
	int i;
	for ( i = 0; i < 4; i += 2 ) {
		y = stdlib_base_kronecker_delta( x[ i ], x[ i+1 ] );
		printf( "kronecker_delta(%lf, %lf) = %lf\n", x[ i ], x[ i+1 ], y );
	}
}
