  #include "stdlib/math/base/special/kronecker_deltaf.h"
#include <stdio.h>

int main( void ) {
	const float x[] = { 3.0f, 4.0f, 5.0f, 5.0f };

	float y;
	int i;
	for ( i = 0; i < 4; i += 2 ) {
		y = stdlib_base_kronecker_deltaf( x[ i ], x[ i+1 ] );
		printf( "kronecker_deltaf(%f, %f) = %f\n", x[ i ], x[ i+1 ], y );
	}
}
