  'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var isnanf = require( '@stdlib/math/base/assert/is-nanf' );
var isPositiveZerof = require( '@stdlib/math/base/assert/is-positive-zerof' );
var PINF = require( '@stdlib/constants/float32/pinf' );
var NINF = require( '@stdlib/constants/float32/ninf' );
var EPS = require( '@stdlib/constants/float32/eps' );
var randu = require( '@stdlib/random/base/randu' );
var tryRequire = require( '@stdlib/utils/try-require' );


// VARIABLES //

var kroneckerDeltaf = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
	'skip': ( kroneckerDeltaf instanceof Error )
};


// TESTS //

tape( 'main export is a function', opts, function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof kroneckerDeltaf, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `0` if `i` does not equal `j`', opts, function test( t ) {
	var x;
	var y;
	var v;
	var i;

	for ( i = 0; i < 1e3; i++ ) {
		x = ( randu()*100.0 ) - 50.0;
		y = ( randu()*100.0 ) - 50.0;
		if ( x === y ) {
			x += EPS;
		}
		v = kroneckerDeltaf( x, y );
		t.equal( isPositiveZerof( v ), true, 'returns 0 when provided '+x+' and '+y );
	}
	t.end();
});

tape( 'the function returns `1` if `i` equals `j`', opts, function test( t ) {
	var values;
	var v;
	var i;

	values = [
		0.0,
		-0.0,
		PINF,
		NINF,
		3.14,
		1.0
	];
	for ( i = 0; i < values.length; i++ ) {
		v = kroneckerDeltaf( values[ i ], values[ i ] );
		t.equal( v, 1.0, 'returns 1 when provided '+values[i]+' and '+values[i] );
	}
	t.end();
});

tape( 'the function returns `NaN` if provided a `NaN`', opts, function test( t ) {
	var v;

	v = kroneckerDeltaf( NaN, 0.0 );
	t.equal( isnanf( v ), true, 'returns NaN' );

	v = kroneckerDeltaf( 0.0, NaN );
	t.equal( isnanf( v ), true, 'returns NaN' );

	v = kroneckerDeltaf( NaN, NaN );
	t.equal( isnanf( v ), true, 'returns NaN' );

	t.end();
});
