  

'use strict';

var incrspace = require( '@stdlib/array/base/incrspace' );
var logaddexp = require( './../lib' );

var x = incrspace( -100.0, 100.0, 1.0 );

var v;
var i;
var j;
for ( i = 0; i < x.length; i++ ) {
	for ( j = i; j < x.length; j++ ) {
		v = logaddexp( x[ i ], x[ j ] );
		console.log( 'x: %d, y: %d, f(x, y): %d', x[ i ], x[ j ], v );
	}
}
