  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var minabs = require( './../lib' );

var x;
var y;
var v;
var i;

for ( i = 0; i < 100; i++ ) {
	x = ( randu()*1000.0 ) - 500.0;
	y = ( randu()*1000.0 ) - 500.0;
	v = minabs( x, y );
	console.log( 'minabs(%d,%d) = %d', x, y, v );
}
