  

'use strict';

// MODULES //

var isNegativeZero = require( '@stdlib/math/base/assert/is-negative-zero' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Returns the minimum and maximum values and assigns results to a provided output array.
*
* @private
* @param {number} x - first number
* @param {number} y - second number
* @param {Collection} out - output array
* @param {integer} stride - output array stride
* @param {NonNegativeInteger} offset - output array index offset
* @returns {Collection} minimum and maximum values
*
* @example
* var out = [ 0.0, 0.0 ];
* var v = minmax( 3.14, 4.2, out, 1, 0 );
* // returns [ 3.14, 4.2 ]
*
* var bool = ( v === out );
* // returns true
*
* @example
* var out = [ 0.0, 0.0 ];
* var v = minmax( 3.14, NaN, out, 1, 0 );
* // returns [ NaN, NaN ]
*
* @example
* var out = [ 0.0, 0.0 ];
* var v = minmax( +0.0, -0.0, out, 1, 0 );
* // returns [ -0.0, 0.0 ]
*/
function minmax( x, y, out, stride, offset ) {
	if ( isnan( x ) || isnan( y ) ) {
		out[ offset ] = NaN;
		out[ offset + stride ] = NaN;
		return out;
	}
	if ( x === y && x === 0.0 ) {
		if ( isNegativeZero( x ) ) {
			out[ offset ] = x;
			out[ offset + stride ] = y;
			return out;
		}
		out[ offset ] = y;
		out[ offset + stride ] = x;
		return out;
	}
	if ( x < y ) {
		out[ offset ] = x;
		out[ offset + stride ] = y;
		return out;
	}
	out[ offset ] = y;
	out[ offset + stride ] = x;
	return out;
}


// EXPORTS //

module.exports = minmax;
