  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var minmaxabs = require( './../lib' );

var x;
var y;
var v;
var i;

for ( i = 0; i < 100; i++ ) {
	x = ( randu()*100.0 ) - 50.0;
	y = ( randu()*100.0 ) - 50.0;
	v = minmaxabs( x, y );
	console.log( 'minmaxabs(%d,%d) = [%d, %d]', x, y, v[0], v[1] );
}
