  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var modf = require( './../lib' );

var parts;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = (randu()*1000.0) - 500.0;
	parts = modf( x );
	console.log( 'modf(%d) => integral: %d. fraction: %d.', x, parts[ 0 ], parts[ 1 ] );
}
