  

import pdifff = require( './index' );


// TESTS //

// The function returns a number...
{
	pdifff( 8, 2 ); // $ExpectType number
}

// The compiler throws an error if the function is provided values other than two numbers...
{
	pdifff( true, 3 ); // $ExpectError
	pdifff( false, 2 ); // $ExpectError
	pdifff( '5', 1 ); // $ExpectError
	pdifff( [], 1 ); // $ExpectError
	pdifff( {}, 2 ); // $ExpectError
	pdifff( ( x: number ): number => x, 2 ); // $ExpectError

	pdifff( 9, true ); // $ExpectError
	pdifff( 9, false ); // $ExpectError
	pdifff( 5, '5' ); // $ExpectError
	pdifff( 8, [] ); // $ExpectError
	pdifff( 9, {} ); // $ExpectError
	pdifff( 8, ( x: number ): number => x ); // $ExpectError

	pdifff( [], true ); // $ExpectError
	pdifff( {}, false ); // $ExpectError
	pdifff( false, '5' ); // $ExpectError
	pdifff( {}, [] ); // $ExpectError
	pdifff( '5', ( x: number ): number => x ); // $ExpectError
}

// The compiler throws an error if the function is provided insufficient arguments...
{
	pdifff(); // $ExpectError
	pdifff( 3 ); // $ExpectError
}
