  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pow = require( './../lib' );

var b;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	b = round( randu()*10.0 );
	x = round( randu()*10.0 ) - 5.0;
	console.log( '%d^%d = %d', b, x, pow( b, x ) );
}
