  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var TWO_PI = require( '@stdlib/constants/float64/two-pi' );
var rad2deg = require( './../lib' );

var r;
var d;
var i;

for ( i = 0; i < 100; i++ ) {
	r = randu() * TWO_PI;
	d = rad2deg( r );
	console.log( 'radians: %d => degrees: %d', r, d );
}
