  

// TypeScript Version: 4.1

/**
* Evaluates the ramp function (single-precision).
*
* ## Notes
*
* -   If `x >= 0`, the function returns `x`; otherwise, the function returns zero.
*
* @param x - input value
* @returns function value
*
* @example
* var v = rampf( 3.14 );
* // returns 3.14
*
* @example
* var v = rampf( -3.14 );
* // returns 0.0
*
* @example
* var v = rampf( NaN );
* // returns NaN
*/
declare function rampf( x: number ): number;


// EXPORTS //

export = rampf;
