  'use strict';

// MODULES //

var isnanf = require( '@stdlib/math/base/assert/is-nanf' );


// MAIN //

/**
* Evaluates the ramp function (single-precision).
*
* @param {number} x - input value
* @returns {number} function value
*
* @example
* var v = rampf( 3.0 );
* // returns 3.0
*
* @example
* var v = rampf( -3.0 );
* // returns 0.0
*
* @example
* var v = rampf( NaN );
* // returns NaN
*/
function rampf( x ) {
	if ( isnanf( x ) ) {
		return NaN;
	}
	if ( x > 0.0 ) {
		return x;
	}
	return 0.0; // handles `-0`
}


// EXPORTS //

module.exports = rampf;
