  

'use strict';

var linspace = require( '@stdlib/array/base/linspace' );
var TWO_PI = require( '@stdlib/constants/float64/two-pi' );
var sin = require( './../lib' );

var x = linspace( 0.0, TWO_PI, 100 );

var i;
for ( i = 0; i < x.length; i++ ) {
	console.log( 'sin(%d) = %d', x[ i ], sin( x[ i ] ) );
}
