  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var sqrt1pm1 = require( './../lib' );

var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = round( randu() * 100.0 );
	console.log( 'sqrt(1+%d) - 1 = %d', x, sqrt1pm1( x ) );
}
