  

'use strict';

var linspace = require( '@stdlib/array/base/linspace' );
var PI = require( '@stdlib/constants/float64/pi' );
var tan = require( './../lib' );

var x = linspace( -PI/2.0, PI/2.0, 100 );

var i;
for ( i = 0; i < x.length; i++ ) {
	console.log( 'tan(%d) = %d', x[ i ], tan( x[ i ] ) );
}
