  'use strict';

// MODULES //

var floorf = require( '@stdlib/math/base/special/floorf' );
var ceilf = require( '@stdlib/math/base/special/ceilf' );


// MAIN //

/**
* Rounds a single-precision floating-point number toward zero.
*
* @param {number} x - input value
* @returns {number} rounded value
*
* @example
* var v = truncf( -4.2 );
* // returns -4.0
*
* @example
* var v = truncf( 9.99999 );
* // returns 9.0
*
* @example
* var v = truncf( 0.0 );
* // returns 0.0
*
* @example
* var v = truncf( -0.0 );
* // returns -0.0
*
* @example
* var v = truncf( NaN );
* // returns NaN
*
* @example
* var v = truncf( Infinity );
* // returns Infinity
*
* @example
* var v = truncf( -Infinity );
* // returns -Infinity
*/
function truncf( x ) {
	if ( x < 0.0 ) {
		return ceilf( x );
	}
	return floorf( x );
}


// EXPORTS //

module.exports = truncf;
