  

'use strict';

// MODULES //

var repl = require( 'repl' );
var stdin = require( '@stdlib/streams/node/stdin' );
var stdout = require( '@stdlib/streams/node/stdout' );


// MAIN //

/**
* Returns default REPL options.
*
* @private
* @returns {Object} default REPL options
*
* @example
* var opts = options();
* // returns {...}
*/
function options() {
	var opts = {
		'prompt': '> ',
		'input': stdin,
		'output': stdout,
		'useGlobal': false,
		'ignoreUndefined': true,
		'replMode': repl.REPL_MODE_SLOPPY
	};
	return opts;
}


// EXPORTS //

module.exports = options;
