tape( 'the command-line interface spawns a REPL', opts, function test( t ) {
	var expected;
	var child;
	var cmd;

	cmd = [
		EXEC_PATH,
		fpath
	];

	child = exec( cmd.join( ' ' ), done );
	child.stdin.write( 'console.log(\'Printing to console...\')\n' );
	child.stdin.write( '3*3' );
	child.stdin.end();

	expected = [
		'> Printing to console...',
		'> 9',
		'> '
	].join( '\n' );

	function done( error, stdout, stderr ) {
		var str;
		var i;
		if ( error ) {
			t.fail( error.message );
		} else {
			str = stdout.toString();
			i = 0;
			while ( i < str.length ) {
				if ( str[ i ] === '>' ) {
					break;
				}
				i += 1;
			}
			str = str.substring( i );
			t.strictEqual( str, expected, 'prints output to `stdout`' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});
