  

'use strict';

var randu = require( '@stdlib/random/base/randu' );
var EPS = require( '@stdlib/constants/float64/eps' );
var mgf = require( './../lib' );

var alpha;
var beta;
var t;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	t = randu() * 20.0;
	alpha = ( randu()*5.0 ) + EPS;
	beta = ( randu()*5.0 ) + EPS;
	v = mgf( t, alpha, beta );
	console.log( 't: %d, α: %d, β: %d, M_X(t;α,β): %d', t.toFixed( 4 ), alpha.toFixed( 4 ), beta.toFixed( 4 ), v.toFixed( 4 ) );
}
